function create_database($db){
	$conn=new mysqli("localhost","root","");
	$q="create database $db";
	$dbn=strtoupper($db);
	if($conn->query($q))
		echo "$dbn - Database $dbn created Successfully <br>";
	else
		echo "$dbn - <font color=red>".$conn->error."</font><br>";
	$conn->close();
}

function run_query($db,$qry){
	$conn=new mysqli("localhost","root","","$db");
	$dbn=strtoupper($db);
	if($conn->query($qry))
		echo "$dbn - Query Runned Successfully <br>";
	else
		echo "$dbn - <font color=red>".$conn->error."</font><br>";
	$conn->close();
}

